content:
html.page-home {
	position: absolute;
	top: 0;
	left: 0;
	right: 0;
	background-repeat: no-repeat;
	background-position: center right;
	/*background-image: url(74.jpg);*/
}


table tr > th {
  font-size: 14px;
}
table tr > td {
  font-size: 13px;
  vertical-align:middle;
}
table th {
  white-space: nowrap;
  text-align: center;
  background-color: #EEE;
}

.content.content-post .article-header .post-title {
  font-size: 28px;
}
.content-home .avatar img {
    box-shadow: 0 10px 10px -10px rgba(0, 0, 0, 0.5);
}

.article-content p {
	font-size: 14px;
  color: #222;
}
.article-content p em {
  font-size: 13px;
}
.article-content table strong {
  color: #333;
}
.article-content img {
	display: inline-block;
  vertical-align: middle;
  -webkit-transform: translateZ(0);
  transform: translateZ(0);
  box-shadow: 0 0 1px rgba(0, 0, 0, 0);
  -webkit-backface-visibility: hidden;
  backface-visibility: hidden;
  -moz-osx-font-smoothing: grayscale;
  -webkit-transition-duration: 0.3s;
  transition-duration: 0.3s;
  -webkit-transition-property: box-shadow;
  transition-property: box-shadow;
  margin: 2px 0;
}
.article-content img:hover, .hvr-shadow:focus, .hvr-shadow:active {
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.6);
}
.article-content ul {
  margin-left: 16px;
  font-size: 13px;
}
